import ItemCardContent from '../components/ItemCardContent'



class ItemCard extends React.Component {
    state = {
        content
    };

      userId = id => {
          const users = this.state.content.map(user => user.id !== id);
          this.setState({ users })
      }
    render() {
        return (
          <div >
              {this.state.users.map(user => (
                  <ItemCardContent 
                  title={user.title}
                  body={user.body}
                  />
              ))}
          </div>
      )
    }
}

export default ItemCard;
