import ItemContent from '../components/ItemCardContent'
import content from '../components/content.json'


class ItemCard extends React.Component {
    state = {
        content
    };

      userId = id => {
          const users = this.state.content.filter(user => user.id !== id);
          this.setState({ users })
      }
    render() {
        return (
          <div >
              {this.state.users.map(user => (
                  <ItemContent 
                  title={user.title}
                  body={user.body}
                  />
              ))}
          </div>
      )
    }
}

export default ItemCard;
